package _02_unbirthday;

import javax.swing.JOptionPane;

public class Unbirthday {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

String birthday	= JOptionPane.showInputDialog("When is your Birthday? mm/dd");	




if( birthday.equals("11/06")){
	

JOptionPane.showMessageDialog(null,"Hey, Happy Birthday!"); 
		
}		
else {		
		
JOptionPane.showMessageDialog(null, "Oh, well happy Unbirthday!"); 		
JOptionPane.showMessageDialog(null, "lol"); 			
		
		
}			
	}

}
